```cpp
#include <iostream>
#include "EasyBMP.h"

using namespace std;

RGBApixel HSVtoRGB(float H, float S, float V) {
    float C = V * S;
    float X = C * (1.0f - std::abs(fmod(H / 60.0f, 2) - 1.0f));
    float m = V - C;
    float Rs, Gs, Bs;

    if (H >= 0 && H < 60) {
        Rs = C;
        Gs = X;
        Bs = 0;
    } else if (H >= 60 && H < 120) {
        Rs = X;
        Gs = C;
        Bs = 0;
    } else if (H >= 120 && H < 180) {
        Rs = 0;
        Gs = C;
        Bs = X;
    } else if (H >= 180 && H < 240) {
        Rs = 0;
        Gs = X;
        Bs = C;
    } else if (H >= 240 && H < 300) {
        Rs = X;
        Gs = 0;
        Bs = C;
    } else {
        Rs = C;
        Gs = 0;
        Bs = X;
    }

    RGBApixel result;
    result.Red = static_cast<unsigned char>((Rs + m) * 255);
    result.Green = static_cast<unsigned char>((Gs + m) * 255);
    result.Blue = static_cast<unsigned char>((Bs + m) * 255);
    result.Alpha = 255;
    return result;
}

int main() {
    int width = 2;
    int height = 1;

    for (int i = 0; i < 256; i++) {
        BMP image;
        image.SetSize(width, height);
        image.SetBitDepth(24);

        float hue = i * 360.0f / 256.0f;
        RGBApixel color = HSVtoRGB(hue, 1.0f, 1.0f);

        for (int x = 0; x < width; x++) {
            for (int y = 0; y < height; y++) {
                image.SetPixel(x, y, color);
            }
        }

        string fileName = "hue_image_" + std::to_string(i) + ".bmp";
        image.WriteToFile(fileName.c_str());
    }

    cout << "Generated 256 2x1-pixel hue images." << endl;
    return 0;
}
